      String? message, 
      List<Data>? data,}){
    _status = status;
    _message = message;
    _data = data;
}

  CourseModel.fromJson(dynamic json) {
    _status = json['status '];
    _message = json['message'];
    if (json['data'] != null) {
      _data = [];
      json['data'].forEach((v) {
        _data?.add(Data.fromJson(v));
      });
    }
  }
  int? _status;
  String? _message;
  List<Data>? _data;

  int? get status => _status;
  String? get message => _message;
  List<Data>? get data => _data;

